$(document).on("mouseenter", ".funnel-segment", function() {
    $(this).find(".segment-text").css("opacity", "1");
    $(this).find(".segment-checkmark").css("opacity", "0");
}).on("mouseleave", ".funnel-segment", function() {
    const statuses = ["Introduction", "Discussion", "Proposal", "Negotiation", "Agreement", "Closed Won", "Closed Lost", "Drop"];
    const current_status_index = statuses.indexOf(cur_frm.doc.status);
    const status_of_this_segment = $(this).data("status");
    const index_of_this_segment = statuses.indexOf(status_of_this_segment);

    if (index_of_this_segment <= current_status_index) {
        $(this).find(".segment-checkmark").css("opacity", "1");
        $(this).find(".segment-text").css("opacity", "0");
    } else {
        $(this).find(".segment-checkmark").css("opacity", "0");
        $(this).find(".segment-text").css("opacity", "1");
    }
});

$(document).on("click", ".funnel-segment", function() {
    const clicked_status = $(this).data("status");
    const frm = cur_frm;

    if (clicked_status !== frm.doc.status) {
        frm.selected_status_for_change = clicked_status;
        $(".funnel-segment").removeClass("selected");
        $(this).addClass("selected");
        $(".save-status-btn").show();
        $(".mark-complete-btn:not(.save-status-btn)").hide();
    } else {
        frm.selected_status_for_change = null;
        $(".funnel-segment").removeClass("selected");
        $(".save-status-btn").hide();
        $(".mark-complete-btn:not(.save-status-btn)").show();
    }
});

$(document).on("click", ".save-status-btn", function() {
    const frm = cur_frm;
    if (frm.selected_status_for_change) {
        frm.set_value("status", frm.selected_status_for_change);
        frm.save();
    }
});

$(document).on("click", ".mark-complete-btn:not(.save-status-btn)", function() {
    const frm = cur_frm;
    const statuses = ["Introduction", "Discussion", "Proposal", "Negotiation", "Agreement", "Closed Won", "Closed Lost", "Drop"];
    const current_status_index = statuses.indexOf(frm.doc.status);
    const next_status_index = current_status_index + 1;

    if (next_status_index < statuses.length) {
        const next_status = statuses[next_status_index];
        frm.set_value("status", next_status);
        frm.save();
    }
});

frappe.ui.form.on('Sample Entry', {
    type_of_sample: function(frm) {
        if (frm.doc.type_of_sample) {
            switch (frm.doc.type_of_sample) {
                case 'Internal Sample':
                case 'Receipt - Packing Material':
                case 'Blending - Premix':
                case 'Filling Line Tea Break Sample':
                case 'Filling Line Lunch Break Sample':
                case 'Filling Line End Break Sample':
                case 'Filling Line Break Sample':
                case 'Tanker Flushing':
                case 'Other':
                case 'Empty Tanker IN':
                    frm.set_value('retention_period', 'NA');
                    break;
                case 'Receipt - SFG':
                case 'Blending':
                case 'First Filled Sample':
                case 'Lab Blend':
                case 'Bulk Filling Tanker Out':
                    frm.set_value('retention_period', '1 Year');
                    break;
                case 'Receipt - Additive':
                    frm.set_value('retention_period', '06 Months');
                    break;
                case 'Reciept- Base oil':
                case 'Blending Kettle Flushing':
                case 'Storage Tank flushing':
                    frm.set_value('retention_period', '03 Months');
                    break;
                default:
                    frm.set_value('retention_period', 'NA');
            }
        }
    }
});

frappe.listview_settings['New Client'] = {
    onload: function(listview) {

        cur_list.page.add_menu_item(__("New Client"), function() {

            frappe.show_alert({
                message: __('Starting Client Sync with Ceipal API...'),
                indicator: 'blue'
            }, 5);

            frappe.call({
                method: "qbs_ats.qbs_ats.doctype.new_client.new_client.custom_method",

                callback: function(response) {
                    if (response.message) {
                        frappe.msgprint({
                            title: __('Sync Complete'),
                            indicator: 'green',
                            message: __(response.message.message || "Clients synced successfully!") 
                        });
                        
                        cur_list.refresh(); 
                    }
                },

                error: function(err) {
                    frappe.msgprint({
                        title: __('Error'),
                        indicator: 'red',
                        message: __('An error occurred during the sync process. Please check the Error Log.')
                    });
                    console.error(err); 
                }
            });
        });
    }
};

frappe.listview_settings['Vendor'] = {
    onload: function(listview) {

