function setup_lead_owner_override(frm) {
    if (!frm.doc.lead_owner || !frm.fields_dict.lead_owner) return;

    let field = frm.fields_dict.lead_owner;
    if (!field.$input || !field.$input.length) return;

    if (frm.doc.lead_owner === "Administrator") {
        field.$input.val("Administrator");
        return;
    }

    frappe.db.get_value("User", frm.doc.lead_owner, "full_name").then(r => {
        if (r && r.message && r.message.full_name) {
            let display_name = r.message.full_name;
            let email = frm.doc.lead_owner;

            field.$input.val(display_name);

            field.$input.off('change.lead_owner_fix');
            field.$input.on('change.lead_owner_fix', function() {
                if (field.$input.val() === email) {
                    field.$input.val(display_name);
                }
            });

            let observer = new MutationObserver(function(mutations) {
                mutations.forEach(function(mutation) {
                    if (mutation.type === 'attributes' && mutation.attributeName === 'value') {
                        if (field.$input.val() === email) {
                            field.$input.val(display_name);
                        }
                    }
                });

                if (field.$input.val() === email) {
                    field.$input.val(display_name);
                }
            });

            if (field.$input[0]) {
                observer.observe(field.$input[0], {
                    attributes: true,
                    attributeFilter: ['value']
                });

                if (!frm._lead_owner_observers) frm._lead_owner_observers = [];
                frm._lead_owner_observers.push(observer);
            }

            if (!frm._lead_owner_interval) {
                frm._lead_owner_interval = setInterval(() => {
                    if (frm.doc.lead_owner === email && field.$input.val() === email) {
                        field.$input.val(display_name);
                    }
                }, 100);
            }

            if (field.awesomplete && field.awesomplete.input) {
                field.awesomplete.input.value = display_name;
            }

            let original_parse = field.parse_validate_and_set_in_model;
            field.parse_validate_and_set_in_model = function(value) {
                if (value === display_name) value = email;
                return original_parse.call(field, value);
            };
        }
    });
}

$(document).on('form-unload', function() {
    frappe.route_hooks.after_load = (frm) => {
        if (frm._lead_owner_interval) clearInterval(frm._lead_owner_interval);
        if (frm._lead_owner_observers) frm._lead_owner_observers.forEach(obs => obs.disconnect());
    };
});

