frappe.ui.form.on('Lead', {
    // --- Auto Calculate Company Age when Establishment Year changes ---
    custom_company_establishment_year: function(frm) {
        if (frm.doc.custom_company_establishment_year) {
            let currentYear = new Date().getFullYear();
            let companyAge = currentYear - frm.doc.custom_company_establishment_year;

            // Save calculated age (optional field)
            // Set boolean or flag if company is 3+ years old
            if (companyAge >= 3) {
                frm.set_value("custom_company_age__3_years", 1);
            } else {
                frm.set_value("custom_company_age__3_years", 0);
            }
        }
    },

    validate: function(frm) {
        if (frm.doc.custom_vertical == "Permanent Staffing") {
            let score = 0;

            // --- Recalculate Company Age before validation ---
            if (frm.doc.custom_company_establishment_year) {
                let currentYear = new Date().getFullYear();
                let companyAge = currentYear - frm.doc.custom_company_establishment_year;
                if (companyAge >= 3) {
                    frm.set_value("custom_company_age__3_years", 1);
                    score += 6;
                } else {
                    frm.set_value("custom_company_age__3_years", 0);
                }
            }

            // . --- Mandatory Do-ability Check ---

            if (!frm.doc.custom_recruitment_doability_approved && frm.doc.status !="New") {
                frm.set_value("custom_qualification_score", 0);
                frm.set_value("status", "Unqualified");
                frappe.msgprint(__('Lead disqualified: Recruitment Do-ability is not approved.'));
                return;
            }

            // 1. --- Eligible Mandates (CTC) Layered Scoring ---
            if (frm.doc.custom_salary_offering) {
                let ctc_score = 0;
                switch (frm.doc.custom_salary_offering.trim()) {
                    case "Less than ₹5 LPA":
                    case "4 to 8 Lacs":
                        ctc_score = 0;
                        break;
                    case "5.01 to 8lakhs":
                        ctc_score = 6;
                        break;
                    case "8 to 12LPA":
                        ctc_score = 8;
                        break;
                    case "12 to 20 LPA":
                        ctc_score = 9;
                        break;
                    case "20.01 and above":
                        ctc_score = 10;
                        break;
                }
                score += ctc_score;
                frm.set_value("custom_eligible_mandate_ctc__6l", ctc_score > 0 ? 1 : 0);
            }

            // 2. --- Verified Client ---
            if (frm.doc.website){ score += 6;
            frm.doc.custom_verified_client_websitedomain_check=1
            }

            // 3. --- Recruitment Do-ability Approved ---
            if (frm.doc.custom_recruitment_doability_approved) score += 10;

            // 4. --- Recent Openings (Positions Open From) Layered Scoring ---
            if (frm.doc.custom_positions_open_from) {
                let opening_score = 0;
                switch (frm.doc.custom_positions_open_from.trim()) {
                    case "Less than 60 Days":
                        opening_score = 10; // <60 days
                        break;
                    case "60 to 120 Days":
                        opening_score = 5; // 60–120 days band
                        break;
                    default:
                        opening_score = 0;
                }
                score += opening_score;
                frm.doc.custom_recent_openings_60_days=1
            }

            // 5. --- Multiple Openings (Recruitment Volume) Layered Scoring ---
            if (frm.doc.custom_recruitment_volume_perm) {
                let volume_score = 0;
                switch (frm.doc.custom_recruitment_volume_perm.trim()) {
                    case "1 to 3":
                        volume_score = 5;
                        break;
                    case "4 to 6":
                        volume_score = 6;
                        break;
                    case "7 and Above":
                        volume_score = 10;
                        break;
                    default:
                        volume_score = 0;
                }
                score += volume_score;
                frm.doc.custom_multiple_openings=1
            }
            // 6. --- Company Type ---
            if (frm.doc.custom_company_type == "Private Limited" || frm.doc.custom_company_type == "Listed") {score += 6;
            frm.doc.custom_company_type_private_ltd__listed=1
            }

            // 7. --- Turnover (Layered Scoring) ---
            if (frm.doc.custom_turnover_in_inr) {
                let turnover_score = 0;
                switch (frm.doc.custom_turnover_in_inr.trim()) {
                    case "Less than 50 Cr":
                        turnover_score = 0;
                        break;
                    case "51 Cr to 150 Cr":
                        turnover_score = 6;
                        break;
                    case "150 Cr to 500 Cr":
                    case "More than 500 Cr":
                    case "More than  500 Cr":
                        turnover_score = 9;
                        break;
                }
                score += turnover_score;
                frm.doc.custom_turnover__50_cr=1
            }

            // 8. --- JD Completeness ---
            if (frm.doc.custom_jd_completeness) score += 8;

            // 13. --- Save Final Score ---
            frm.set_value("custom_qualification_score", score);

            // 14. --- Optional Status Update ---
            // Uncomment if you want to auto-set Lead Status
            /*
            if (score >= 53) {
                frm.set_value("status", "Qualified");
            } else {
                frm.set_value("status", "Unqualified");
            }
            */

            frappe.msgprint(__('Qualification Score calculated: {0}', [score]));
        }
    }
});

