function set_status_dates(frm) {
    const status = frm.doc.status;

    if (status === "Convert") {
        if (!frm.doc.custom_convert_on) {
            frm.set_value('custom_convert_on', frappe.datetime.now_datetime(), () => {
                calculate_lead_age(frm);
            });
        } else {
            calculate_lead_age(frm);
        }
    } else if (["Qualified", "Converted"].includes(status)) {
        if (!frm.doc.custom_custom_qualified_on) {
            frm.set_value('custom_custom_qualified_on', frappe.datetime.now_datetime(), () => {
                calculate_lead_age(frm);
            });
        } else {
            calculate_lead_age(frm);
        }
    } else if (status === "Unqualified") {
        if (!frm.doc.custom_unqualified_on) {
            frm.set_value('custom_unqualified_on', frappe.datetime.now_datetime(), () => {
                calculate_lead_age(frm);
            });
        } else {
            calculate_lead_age(frm);
        }
    } else {
        calculate_lead_age(frm);
    }
}

function calculate_lead_age(frm) {
    if (!frm.doc.custom_created_on) return;

    let diff_days = 0;
    const created_on = new Date(frm.doc.custom_created_on);
    let end_date;
    let show_age = false;

    if (["New", "Working", "Nurturing"].includes(frm.doc.status)) {
        // Active leads → calculate age until today
        end_date = new Date();
        show_age = true;
    } else if (frm.doc.status === "Convert") {
        // Convert status → only if custom_convert_on exists
        if (frm.doc.custom_convert_on) {
            end_date = new Date(frm.doc.custom_convert_on);
            show_age = true;
        }
    } else if (["Qualified", "Converted"].includes(frm.doc.status)) {
        // Qualified leads → only if custom_custom_qualified_on exists
        if (frm.doc.custom_custom_qualified_on) {
            end_date = new Date(frm.doc.custom_custom_qualified_on);
            show_age = true;
        }
    } else if (frm.doc.status === "Unqualified") {
        // Unqualified leads → only if custom_unqualified_on exists
        if (frm.doc.custom_unqualified_on) {
            end_date = new Date(frm.doc.custom_unqualified_on);
            show_age = true;
        }
    }

    if (show_age) {
        diff_days = Math.floor((end_date - created_on) / (1000 * 60 * 60 * 24));
        diff_days = diff_days > 0 ? diff_days : 0;
    }

    frm.set_df_property('custom_lead_age', 'hidden', !show_age);
    frm.set_value('custom_lead_age', diff_days);
}

