                            field.$input.val(display_name);
                        }
                    }
                });

                if (field.$input.val() === email) {
                    field.$input.val(display_name);
                }
            });

            if (field.$input[0]) {
                observer.observe(field.$input[0], {
                    attributes: true,
                    attributeFilter: ['value']
                });

                if (!frm._lead_owner_observers) frm._lead_owner_observers = [];
                frm._lead_owner_observers.push(observer);
            }

            if (!frm._lead_owner_interval) {
                frm._lead_owner_interval = setInterval(() => {
                    if (frm.doc.opportunity_owner === email && field.$input.val() === email) {
                        field.$input.val(display_name);
                    }
                }, 100);
            }

            if (field.awesomplete && field.awesomplete.input) {
                field.awesomplete.input.value = display_name;
            }

            let original_parse = field.parse_validate_and_set_in_model;
            field.parse_validate_and_set_in_model = function(value) {
                if (value === display_name) value = email;
                return original_parse.call(field, value);
            };
        }
    });
}

