// --- Checklist dialog function ---
function open_checklist_dialog(frm, next_status) {
    const d = new frappe.ui.Dialog({
        title: __('Permanent Staffing Checklist'),
        fields: [
            { fieldtype: 'Section Break', label: 'Checklist' },
            // --- COLUMN 1 ---
            { label: 'Eligible Mandate (CTC ≥ ₹6L)', fieldname: 'custom_eligible_mandate_ctc__6l', fieldtype: 'Check', default: frm.doc.custom_eligible_mandate_ctc__6l },
            { label: 'Verified Client (Website/Domain Check)', fieldname: 'custom_verified_client_websitedomain_check', fieldtype: 'Check', default: frm.doc.custom_verified_client_websitedomain_check },
            { label: 'Recent Openings (≤60 days)', fieldname: 'custom_recent_openings_60_days', fieldtype: 'Check', default: frm.doc.custom_recent_openings_60_days },
            { label: 'Location Tier (Metro / Tier 1 / Tier 2)', fieldname: 'custom_location_tier_metro__tier_1__tier_2_', fieldtype: 'Check', default: frm.doc.custom_location_tier_metro__tier_1__tier_2_ },
            { label: 'Company Type (Private Ltd / Listed)', fieldname: 'custom_company_type_private_ltd__listed', fieldtype: 'Check', default: frm.doc.custom_company_type_private_ltd__listed },
            { label: 'JD Completeness', fieldname: 'custom_jd_completeness', fieldtype: 'Check', default: frm.doc.custom_jd_completeness },
            { fieldtype: 'Column Break' },
            // --- COLUMN 2 ---
            { label: 'Company Age ≥ 3 years', fieldname: 'custom_company_age__3_years', fieldtype: 'Check', default: frm.doc.custom_company_age__3_years },
            { label: 'Recruitment Do-ability Approved', fieldname: 'custom_recruitment_doability_approved', fieldtype: 'Check', default: frm.doc.custom_recruitment_doability_approved },
            { label: 'Target Industry', fieldname: 'custom_target_industry_', fieldtype: 'Check', default: frm.doc.custom_target_industry_ },
            { label: 'Multiple Openings', fieldname: 'custom_multiple_openings', fieldtype: 'Check', default: frm.doc.custom_multiple_openings },
            { label: 'Turnover ≥ ₹50 Cr', fieldname: 'custom_turnover__50_cr', fieldtype: 'Check', default: frm.doc.custom_turnover__50_cr },
            { label: 'Budget Clarity', fieldname: 'custom_budget_clarity', fieldtype: 'Check', default: frm.doc.custom_budget_clarity },
        ],
        primary_action(values) {
    d.hide();

    // Save checklist values
    Object.keys(values).forEach(fieldname => {
        frm.set_value(fieldname, values[fieldname] ? 1 : 0);
    });

    let score = 0;

    // If Recruitment Do-ability is not approved → show confirmation before marking Unqualified
    if (!values.custom_recruitment_doability_approved) {
        frappe.confirm(
            __('Lead is Unqualified because Recruitment Do-ability is not approved. Do you want to proceed?'),
            function() {
                // User clicked Yes
                frm.set_value("custom_qualification_score", 0);
                frm.set_value("status", "Unqualified");
                frm.set_value("custom_unqualified_reason", "Do-ability issue");
                frm.toggle_display("custom_qualification_score", true);

                frappe.show_alert(__('Lead marked as Unqualified.'), 5);

                setTimeout(() => frm.save('Save'), 100);
            },
            function() {
                // User clicked No → do nothing
                frappe.show_alert(__('Action cancelled. Lead status changed.'), 5);
                frm.set_value("status", "Nurturing");
                setTimeout(() => {
                    frm.save('Save').then(() => {
                        frm.reload_doc();
                    });
                }, 100);
            }
        );
        return;
    }

    // If approved → calculate score
    if (values.custom_eligible_mandate_ctc__6l) score += 10;
    if (values.custom_verified_client_websitedomain_check) score += 6;
    if (values.custom_company_age__3_years) score += 6;
    if (values.custom_recruitment_doability_approved) score += 10;
    if (values.custom_recent_openings_60_days) score += 8;
    if (values.custom_target_industry_) score += 7;
    if (values.custom_location_tier_metro__tier_1__tier_2_) score += 7;
    if (values.custom_multiple_openings) score += 8;
    if (values.custom_company_type_private_ltd__listed) score += 6;
    if (values.custom_turnover__50_cr) score += 5;
    if (values.custom_jd_completeness) score += 8;
    if (values.custom_budget_clarity) score += 6;

    // Mark lead as qualified
    frm.set_value("custom_qualification_score", score);
    frm.set_value("status", "Qualified");
    frm.toggle_display("custom_qualification_score", true);

    frappe.show_alert(__('Checklist saved successfully. Qualification Score: {0}', [score]), 5);

    setTimeout(() => {
        frm.save('Save', null, null, () => {
            console.log('Lead saved successfully after checklist completion');
        });
    }, 100);


        }
    });

    d.show();
}

