erpnext.CustomLeadController = class CustomLeadController extends erpnext.LeadController {
	refresh() {
		// ✅ Keep ERPNext's original behavior (Notes, Activities, Address, etc.)
		super.refresh();

		const frm = this.frm;
		const doc = frm.doc;

		// ✅ Always remove ERPNext’s default "Create Opportunity" button
		frm.remove_custom_button("Opportunity", "Create");

		// ✅ Remove entire "Create" menu by default
		frm.clear_custom_buttons();

		// ✅ Show custom Create menu only for Convert / Converted status
		if (!frm.is_new() && ["Convert", "Converted"].includes(doc.status)) {
			frm.add_custom_button(__("Opportunity"), this.make_opportunity.bind(this), __("Create"));
		}
	}

	async make_opportunity() {
		const frm = this.frm;

		let existing_prospect = (
			await frappe.db.get_value(
				"Prospect Lead",
				{ lead: frm.doc.name },
				"name",
				null,
				"Prospect"
			)
		).message?.name;

		await frm.reload_doc();

		let existing_contact = (
			await frappe.db.get_value(
				"Contact",
				{
					first_name: frm.doc.first_name || frm.doc.lead_name,
					last_name: frm.doc.last_name,
				},
				"name"
			)
		).message?.name;

		let fields = [];

		fields.push({
			fieldtype: "HTML",
			fieldname: "info_section",
			options: `
				<ul style="margin-top:10px;">
					<li>✅ Customer Created</li>
					<li>✅ Contact Created</li>
				</ul>
			`
		});

		if (!existing_prospect) {
			fields.push({
				label: "Create Opportunity",
				fieldname: "create_opportunity",
				fieldtype: "Check",
				default: 0
			});
		}

		const d = new frappe.ui.Dialog({
			title: __("Convert Lead"),
			fields: fields,
			primary_action_label: __("Create"),
			primary_action(data) {
				if (data.create_opportunity) {
					frappe.call({
						method: "create_prospect_and_contact",
						doc: frm.doc,
						args: { data },
						freeze: true,
						callback(r) {
							if (!r.exc) {
								frappe.model.open_mapped_doc({
									method: "erpnext.crm.doctype.lead.lead.make_opportunity",
									frm: frm,
								});
							}
						},
					});
				} else {
					frappe.msgprint(__("Customer and Contact Created Successfully. Opportunity not created."));
				}
				d.hide();
			},
		});
		d.show();
	}
};

