frappe.ui.form.on('Lead', {
    validate: function(frm) {
        if (frm.doc.custom_vertical == "Franchise" && frm.doc.status != "New") {
            let score = 0;
            let missing_required = false;

            // --- 1. Mandatory Checks ---
            const mandatory_fields = [
                "custom_recruitment_investment",
                "custom_financial_readiness",
                "custom_team_hiring"
            ];

            mandatory_fields.forEach(field => {
                if (!frm.doc[field]) {
                    missing_required = true;
                }
            });

            // --- 2. If any mandatory missing, set score = 0 and mark unqualified ---
            if (missing_required) {
                score = 0;
                frm.set_value("custom_qualification_score", score);
                frm.set_value("status", "Unqualified");
                frappe.msgprint({
                    title: __("Missing Information"),
                    message: __("Please complete all mandatory fields: Recruitment Investment, Financial Readiness, and Team Hiring."),
                    indicator: "red"
                });
                return; // stop further calculation
            }

            // --- 3. Optional scoring logic ---
            if (frm.doc.custom_local_connections) score += 8;

            if (frm.doc.custom_office_space_franchise) {
                score += 7;
                frm.doc.custom_office_space = 1;
            }

            if (frm.doc.custom_recruitment_investment) score += 10;
            if (frm.doc.custom_target_city) score += 9;
            if (frm.doc.custom_financial_readiness) score += 10;
            if (frm.doc.custom_team_hiring) score += 10;

            if (frm.doc.custom__franchisee_engagement_intent) {
                let volume_score = 0;
                switch (frm.doc.custom__franchisee_engagement_intent.trim()) {
                    case "Full-time (Active Operational Involvement)":
                        volume_score = 10;
                        break;
                    case "Part-time (Supervisory / Oversight Role)":
                        volume_score = 7;
                        break;
                    case "Undecided / To be discussed":
                        volume_score = 3;
                        break;
                    default:
                        volume_score = 0;
                }
                score += volume_score;
                frm.doc.custom_active_involvement = 1;
            }

            // --- 4. Save Final Score ---
            frm.set_value("custom_qualification_score", score);

            // --- 5. Optional Auto-status based on score ---
            if (score >= 53) {
                frm.set_value("status", "Qualified");
            } else {
                frm.set_value("status", "Unqualified");
            }

            // --- 6. Message ---
            frappe.msgprint(__('Qualification Score calculated: {0}', [score]));
        }
    }
});

