            // --- 1. Company Age ---
            if (frm.doc.custom_company_establishment_year) {
                let currentYear = new Date().getFullYear();
                let companyAge = currentYear - frm.doc.custom_company_establishment_year;
                if (companyAge >= 3) {
                    frm.set_value("custom_company_establishment", 1);
                    score += 6;
                } else {
                    frm.set_value("custom_company_establishment", 0);
                }
            }

            // --- 2. Compliance scope (PF/ESI etc.) ---
            if (frm.doc.custom_compliance_scope_pfesi_etc) score += 10;

            // --- 3. Minimum Wages ---
            if (frm.doc.custom_minimum_wages) score += 7;

            // --- 4. Client Due Diligence ---
            if (frm.doc.custom_client_due_diligence) score += 8;

            // --- 5. Recruitment Do-ability Approved ---
            if (frm.doc.custom_recruitment_doability_approved_temp) score += 10;

            // --- 6. Company Type ---
            if (frm.doc.custom_company_type == "Private Limited" || frm.doc.custom_company_type == "Listed") {
                score += 6;
                frm.doc.custom_company_temp = 1;
            }

            // --- 7. Employment Tenure ---
            if (frm.doc.custom_employment_tenure_temp > 1) {
                score += 6;
                frm.doc.custom_employment_tenure = 1;
            }

            // --- 8. Minimum Staffing Requirement ---
            if (frm.doc.custom_recruitment_volume) {
                let volume_score = 0;
                switch (frm.doc.custom_recruitment_volume.trim()) {
                    case "10 to 50":
                        volume_score = 6;
                        break;
                    case "51 to 100":
                        volume_score = 9;
                        break;
                    case "Above 100":
                        volume_score = 10;
                        break;
                }
                score += volume_score;
                frm.doc.custom_multilocation_presence = 1;
            }

            // --- 9. Turnover ---
            if (frm.doc.custom_turnover_in_inr) {
                let turnover_score = 0;
                switch (frm.doc.custom_turnover_in_inr.trim()) {
                    case "Less than 50 Cr":
                        turnover_score = 0;
                        break;
                    case "50 Cr to 200 Cr":
                        turnover_score = 6;
                        break;
                    case "200 and above":
                        turnover_score = 9;
                        break;
                }
                score += turnover_score;
                frm.doc.custom_turnover_temp = 1;
            }

            // --- 10. Save Final Score ---
            frm.set_value("custom_qualification_score", score);

            // --- 11. Auto Status Update ---
            if (score >= 50) {
                frm.set_value("status", "Qualified");
            } else {
                frm.set_value("status", "Unqualified");
            }

            frappe.msgprint(__('Qualification Score calculated: {0}', [score]));
        }
    },

    refresh: function(frm) {
        // Dynamically mark mandatory fields as required
        if (frm.doc.custom_vertical == "Temporary Staffing") {
            [
                "custom_employment_tenure_temp",
                "custom_recruitment_doability_approved_temp",
                "custom_minimum_wages",
                "custom_compliance_scope_pfesi_etc"
            ].forEach(field => frm.set_df_property(field, "reqd", 1));
        } else {
            [
                "custom_employment_tenure_temp",
                "custom_recruitment_doability_approved_temp",
                "custom_minimum_wages",
                "custom_compliance_scope_pfesi_etc"
            ].forEach(field => frm.set_df_property(field, "reqd", 0));
        }
    }
});

frappe.ui.form.on('Lead', {
    validate: function(frm) {
        if (frm.doc.custom_vertical == "Learning & Development" && frm.doc.status != "New") {
            let score = 0;

            // --- 1. Mandatory Field Check ---
            if (!frm.doc.no_of_employees || frm.doc.no_of_employees < 5) {
                frm.set_value("custom_qualification_score", 0);
                frm.set_value("status", "Unqualified");
                refresh_field("status"); // force update

                frappe.msgprint({
                    title: __("Missing Information"),
                    message: __("Please specify at least 5 Employees / Participants before qualification scoring."),
                    indicator: "red"
                })
                return;
            }

            // --- 2. Trainer Availability / Scheduling Fit ---
            if (frm.doc.custom_trainer_availability__scheduling_fit) score += 9;

            // --- 3. Direct Delivery ---
            if (frm.doc.custom_direct_delivery_no_subcontracting) score += 9;

            // --- 4. B2B Engagement ---
            if (frm.doc.custom_b2b_engagement) score += 7;

            // --- 5. Number of Employees / Participants ---
            if (frm.doc.no_of_employees >= 5) {
                score += 7;
                frm.doc.custom_number_of_employees__participants = 1;
            }

            // --- 6. Turnover ---
            if (frm.doc.custom_turnover_in_inr) {
                let volume_score = 0;
                switch (frm.doc.custom_turnover_in_inr.trim()) {
                    case "Less than 50 Cr":
                        volume_score = 5;
                        break;
                    case "50 Cr to 200 Cr":
                        volume_score = 7;
                        break;
                    case "200 and above":
                        volume_score = 9;
                        break;
                }
                score += volume_score;
                frm.doc.custom_turnover_ld = 1;
            }

            // --- 7. Requested Training Timeline ---
            if (frm.doc.custom_requested_training_timeline) {
                let volume_score = 0;
                switch (frm.doc.custom_requested_training_timeline.trim()) {
                    case "Less Than 30 Days":
                        volume_score = 10;
                        break;
                    case "30 days to 60 Days":
                        volume_score = 8;
                        break;
                    case "60 Days and Above":
                        volume_score = 6;
                        break;
                }
                score += volume_score;
                frm.doc.custom_urgency__timeline = 1;
            }

            // --- 8. Save Final Score ---
            frm.set_value("custom_qualification_score", score);

            // --- 9. Auto-Status Update ---
            if (score >= 40) {
                frm.set_value("status", "Qualified");
            } else {
                frm.set_value("status", "Unqualified");
            }

            frappe.msgprint(__('Qualification Score calculated: {0}', [score]));
        }
    },

    refresh: function(frm) {
        // Dynamically make Number of Employees mandatory only for L&D vertical
        if (frm.doc.custom_vertical == "Learning & Development") {
            frm.set_df_property("no_of_employees", "reqd", 1);
        } else {
            frm.set_df_property("no_of_employees", "reqd", 0);
        }
    }
});

frappe.ui.form.on('Opportunity', {
    status: function(frm) {
        // Define status to probability mapping
        const status_probability = {
            'Proposal': 40,
            'Negotiation': 70,
            'Agreement': 90,
            'Closed Won': 100
        };

        // Get current status
        let status = frm.doc.status;

        // Set probability based on status
        if (status in status_probability) {
            frm.set_value('probability', status_probability[status]);
        }
    },

    refresh: function(frm) {
        // Set probability to 0 on new form
        if (frm.is_new() && !frm.doc.probability) {
            frm.set_value('probability', 0);
        }
    }
});
