                .funnel-segment .segment-text { opacity: 1; transition: opacity 0.3s ease; }
                .funnel-segment .segment-checkmark { opacity: 0; position: absolute; transition: opacity 0.3s ease; }
                .funnel-segment.completed .segment-checkmark, .funnel-segment.active .segment-checkmark { opacity: 1; }
                .funnel-segment.completed .segment-text, .funnel-segment.active .segment-text { opacity: 0; }
                .funnel-segment:not(:last-child) { clip-path: polygon(0% 0%, calc(100% - 20px) 0%, 100% 50%, calc(100% - 20px) 100%, 0% 100%, 20px 50%); }
                .funnel-segment:first-child { clip-path: polygon(0% 0%, calc(100% - 20px) 0%, 100% 50%, calc(100% - 20px) 100%, 0% 100%); border-radius: 6px 0 0 6px; }
                .funnel-segment:last-child { clip-path: polygon(0% 0%, 100% 0%, 100% 100%, 0% 100%, 20px 50%); border-radius: 0 6px 6px 0; margin-right: 0; }

                .funnel-segment.completed { background: #10b981; color: white; box-shadow: 0 2px 4px rgba(16,185,129,0.3); }
                .funnel-segment.active { background: #3b82f6; color: white; box-shadow: 0 2px 6px rgba(59,130,246,0.4); transform: scale(1.02); }
                .funnel-segment:hover { transform: translateY(-1px); box-shadow: 0 4px 8px rgba(0,0,0,0.15); }
                .funnel-segment.completed:hover { box-shadow: 0 4px 8px rgba(16,185,129,0.4); }

                .mark-complete-btn { background: linear-gradient(135deg,#3b82f6,#1d4ed8); color: white; text-align: center; padding: 15px; border-radius: 8px; font-weight: 600; cursor: pointer; margin-top: 10px; user-select: none; transition: all 0.3s ease; box-shadow: 0 2px 4px rgba(59,130,246,0.3); }
                .mark-complete-btn:hover { background: linear-gradient(135deg,#2563eb,#1e40af); transform: translateY(-1px); box-shadow: 0 4px 8px rgba(59,130,246,0.4); }
            </style>

            <div class="custom-lead-header">
                <div class="lead-title-bar">
                    <div class="lead-left-section">
                        <div class="lead-icon">â˜…</div>
                        <div class="lead-badge">Opportunity</div>
                        <div class="lead-name">${frm.doc.opportunity_from || ""} ${frm.doc.customer_name || ""}</div>
                    </div>
                </div>
                <div class="lead-details-grid">
                    <div class="detail-item"><span class="detail-label">Company</span><span class="detail-value">${frm.doc.company_name || "Octa Private Limited"}</span></div>
                    <div class="detail-item"><span class="detail-label">Designation</span><span class="detail-value">${frm.doc.contact_designation || "Manager - operations"}</span></div>
                    <div class="detail-item"><span class="detail-label">Phone</span><span class="detail-value">${frm.doc.phone || "9123456789"}</span></div>
                    <div class="detail-item"><span class="detail-label">Email</span><span class="detail-value">${frm.doc.email_id || "hr_operations@youstrong.in"}</span></div>
