      if (index < current_status_index) {
        segment_class = "completed"
        tooltip_text = `${status} - Completed`
      } else if (index === current_status_index) {
        if (status === "Closed Lost") {
          segment_class = "active"
        } else if (status === "Closed Won") {
          // âœ… Active hone par green ho jaye
          segment_class = "completed"
        } else {
          segment_class = "completed"
        }
        tooltip_text = `${status} - Current Stage`
      } else {
        tooltip_text = `${status} - Pending`
